mapper.select:
select g.*,gp.minprice,gpay.paycount from goods g inner join (select min(currprice) minprice,gid from goodsprice group by gid) gp on g.id=gp.gid inner join (select count(*) paycount,og.gid from (select od.id,od.gid from orderdetail od inner join (select id from goodsorder where flag between 2 and 7) o on od.oid=o.id) og GROUP BY og.gid) gpay on gpay.gid=g.id
and gtid=#{gtid}
and brand=#{brand}
order by paycount desc

    select * from (select gs.gavalue,gs.gid,gs.id gsid,ga.name skuname from goodssku gs inner join goodsattribute ga on gs.gaid=ga.id where gid=#{id}) gsku inner join
(select g.*,gp.minprice,gp.maxprice from goods g inner join (select min(currprice) minprice,max(currprice) maxprice,gid from goodsprice group by gid) gp
on g.id=gp.gid) gd on gsku.gid=gd.id where id=#{id};
